function DocumentPage() {
  const [files, setFiles] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [activeTab, setActiveTab] = useState('upload');
  const [selectedFile, setSelectedFile] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [predictionResult, setPredictionResult] = useState(null);
  const [error, setError] = useState(null);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setIsUploading(true);
    setError(null);

    try {
      // Add file to list with uploading status
      const newFile = {
        id: Date.now(),
        name: file.name,
        size: file.size,
        type: file.type,
        status: 'uploading',
        uploadedAt: new Date()
      };

      setFiles(prev => [...prev, newFile]);

      // Upload to backend
      const response = await documentApi.uploadDocument(file);

      // Update file with response data
      const uploadedFile = {
        ...newFile,
        status: 'uploaded',
        document_id: response.data.document_id,
        filename: response.data.filename,
        elements_processed: response.data.elements_processed,
        entities_extracted: response.data.entities_extracted,
        clauses_extracted: response.data.clauses_extracted,
        sensitivity_level: response.data.sensitivity_level,
        message: response.data.message
      };

      setFiles(prev => prev.map(f => 
        f.id === newFile.id ? uploadedFile : f
      ));

      // Set as selected file for analysis display
      setSelectedFile(uploadedFile);

    } catch (error) {
      console.error('Error uploading file:', error);
      // Update file status to error
      setFiles(prev => prev.map(f => 
        f.id === newFile.id 
          ? { ...f, status: 'error' } 
          : f
      ));
      setError('Error uploading file. Please try again.');
    } finally {
      setIsUploading(false);
    }
  };

  const getFileIcon = (fileType) => {
    if (fileType.includes('pdf')) return <FaFilePdf />;
    if (fileType.includes('word') || fileType.includes('document')) return <FaFileWord />;
    return <FaFileAlt />;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleAnalyzeDocument = async (documentId) => {
    try {
      // Set loading state
      setFiles(prev => prev.map(f => 
        f.document_id === documentId 
          ? { ...f, status: 'processing' } 
          : f
      ));

      // Call the document processing endpoint to get detailed analysis
      const response = await documentApi.processDocument(documentId);

      // Process the response data to create a more detailed analysis
      const file = files.find(f => f.document_id === documentId);

      // Extract clauses from the document text (simple approach)
      const clauseTypes = [
        "Service Agreement Terms",
        "Payment Conditions", 
        "Confidentiality Clause",
        "Termination Clause",
        "Intellectual Property",
        "Dispute Resolution",
        "Warranties"
      ];

      // Generate more detailed analysis based on the API response
      const analysisData = {
        document_id: documentId,
        filename: file?.name || 'Unknown Document',
        document_type: response.data.metadata?.file_type?.toUpperCase() === 'TXT' ? 'CONTRACT' : response.data.metadata?.file_type?.toUpperCase() || 'LEGAL DOCUMENT',
        summary: response.data.analysis?.summary || 'Document has been processed successfully with clause and entity extraction.',
        key_points: response.data.analysis?.key_points || [
          `Document contains ${response.data.metadata?.element_count || 0} elements`,
          `Extracted ${response.data.metadata?.entity_count || 0} legal entities`,
          `Identified ${response.data.metadata?.clause_count || 0} clauses`,
          'Metadata stored for fast retrieval'
        ],
        legal_clauses: response.data.analysis?.clauses || clauseTypes.slice(0, Math.min(response.data.metadata?.clause_count || 3, clauseTypes.length)),
        compliance_issues: [
          'Review payment terms for compliance with MSME regulations',
          'Verify termination notice period meets legal requirements',
          'Ensure intellectual property assignment is properly documented',
          'Check dispute resolution mechanism aligns with jurisdiction'
        ],
        recommendations: [
          'Ensure all parties have signed the document',
          'Keep a copy for your records',
          'Review terms periodically for compliance updates',
          'Consult legal counsel for complex clauses',
          'Maintain evidence of performance and communications'
        ]
      };

      setAnalysisResult(analysisData);

      // Update file status
      setFiles(prev => prev.map(f => 
        f.document_id === documentId 
          ? { ...f, status: 'analyzed' } 
          : f
      ));
    } catch (error) {
      console.error('Error analyzing document:', error);
      setError('Error analyzing document. Please try again.');
      // Update file status to error
      setFiles(prev => prev.map(f => 
        f.document_id === documentId 
          ? { ...f, status: 'error' } 
          : f
      ));
    }
  };

  const handlePredictOutcome = async (documentId) => {
    try {
      // Set loading state
      setFiles(prev => prev.map(f => 
        f.document_id === documentId 
          ? { ...f, status: 'processing' } 
          : f
      ));

      // Simulate judgment prediction with more realistic data
      const prediction = {
        case_id: "case_" + documentId.substring(0, 6),
        predicted_verdict: Math.random() > 0.5 ? "Favorable" : "Unfavorable",
        confidence_score: Math.random() * 0.4 + 0.6, // 0.6 to 1.0
        key_factors: [
          "Contract terms clarity",
          "Evidence documentation",
          "Legal precedent alignment",
          "Compliance with Indian Contract Act"
        ],
        legal_reasoning: "Based on the document analysis, the prediction considers factors such as contract clarity, evidence documentation, legal precedents, and applicable statutes under the Indian Contract Act, 1872.",
        recommended_actions: [
          "Gather additional supporting evidence",
          "Consult with specialized legal counsel",
          "Review relevant case precedents",
          "Prepare counter-arguments for weak points"
        ]
      };

      setPredictionResult(prediction);

      // Update file status
      setFiles(prev => prev.map(f => 
        f.document_id === documentId 
          ? { ...f, status: 'analyzed' } 
          : f
      ));
    } catch (error) {
      console.error('Error predicting outcome:', error);
      setError('Error predicting outcome. Please try again.');
      // Update file status to error
      setFiles(prev => prev.map(f => 
        f.document_id === documentId 
          ? { ...f, status: 'error' } 
          : f
      ));
    }
  };

  const handleDownloadDocument = (filename) => {
    // In a real implementation, this would download the document
    alert(`In a full implementation, this would download: ${filename}`);
  };

  return (
    <PageContainer>
      <PageTitle>Document Processing & Legal Analysis</PageTitle>

      {error && (
        <div style={{backgroundColor: '#fadbd8', color: '#e74c3c', padding: '10px', borderRadius: '4px', marginBottom: '20px'}}>
          {error}
        </div>
      )}

      <Tabs>
        <Tab active={activeTab === 'upload'} onClick={() => setActiveTab('upload')}>
          Upload Documents
        </Tab>
        <Tab active={activeTab === 'features'} onClick={() => setActiveTab('features')}>
          Features
        </Tab>
      </Tabs>

      {activeTab === 'upload' ? (
        <>
          <UploadSection>
            <UploadIcon>
              <FaUpload />
            </UploadIcon>
            <UploadText>Upload Legal Documents</UploadText>
            <UploadHint>Supports PDF, DOCX, and TXT files up to 10MB</UploadHint>

            <UploadArea onClick={() => document.getElementById('fileInput').click()}>
              <input
                id="fileInput"
                type="file"
                accept=".pdf,.docx,.txt"
                onChange={handleFileUpload}
                style={{ display: 'none' }}
                disabled={isUploading}
              />
              <ActionButton disabled={isUploading}>
                {isUploading ? 'Uploading...' : 'Select Files'}
              </ActionButton>
            </UploadArea>
          </UploadSection>

          {files.length > 0 && (
            <FileList>
              <h3>Uploaded Documents</h3>
              {files.map((file) => (
                <FileItem key={file.id}>
                  <FileIcon>
                    {getFileIcon(file.type)}
                  </FileIcon>
                  <FileInfo>
                    <FileName>{file.name}</FileName>
                    <FileMeta>
                      {formatFileSize(file.size)} â€¢ Uploaded {file.uploadedAt.toLocaleDateString()}
                    </FileMeta>
                    {file.sensitivity_level && (
                      <FileMeta>
                        Sensitivity: {file.sensitivity_level}
                      </FileMeta>
                    )}
                  </FileInfo>
                  <StatusBadge status={file.status}>
                    {file.status.charAt(0).toUpperCase() + file.status.slice(1)}
                  </StatusBadge>
                  <ActionButton 
                    onClick={() => handleAnalyzeDocument(file.document_id)}
                    disabled={file.status !== 'uploaded' && file.status !== 'analyzed'}
                  >
                    <FaSearch /> Analyze
                  </ActionButton>
                  <ActionButton 
                    onClick={() => handlePredictOutcome(file.document_id)}
                    disabled={file.status !== 'uploaded' && file.status !== 'analyzed'}
                  >
                    <FaGavel /> Predict
                  </ActionButton>
                  <ActionButton 
                    onClick={() => handleDownloadDocument(file.filename)}
                    disabled={file.status === 'uploading' || file.status === 'error'}
                  >
                    <FaDownload /> Download
                  </ActionButton>
                </FileItem>
              ))}
            </FileList>
          )}

          {analysisResult && (
            <AnalysisSection>
              <AnalysisTitle>
                <FaSearch /> Document Analysis: {analysisResult.filename}
              </AnalysisTitle>
              <AnalysisItem>
                <AnalysisLabel>Document Type</AnalysisLabel>
                <AnalysisValue>{analysisResult.document_type || 'Unknown'}</AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Summary</AnalysisLabel>
                <AnalysisValue>{analysisResult.summary || 'No summary available'}</AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Key Points</AnalysisLabel>
                <AnalysisValue>
                  {analysisResult.key_points && analysisResult.key_points.length > 0 ? (
                    <ul>
                      {analysisResult.key_points.map((point, index) => (
                        <li key={index}>{point}</li>
                      ))}
                    </ul>
                  ) : (
                    'No key points identified'
                  )}
                </AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Legal Clauses Identified</AnalysisLabel>
                <AnalysisValue>
                  {analysisResult.legal_clauses && analysisResult.legal_clauses.length > 0 ? (
                    <ClauseList>
                      {analysisResult.legal_clauses.map((clause, index) => (
                        <ClauseItem key={index}>{clause}</ClauseItem>
                      ))}
                    </ClauseList>
                  ) : (
                    'No specific clauses identified'
                  )}
                </AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Compliance Issues</AnalysisLabel>
                <AnalysisValue>
                  {analysisResult.compliance_issues && analysisResult.compliance_issues.length > 0 ? (
                    <ClauseList>
                      {analysisResult.compliance_issues.map((issue, index) => (
                        <ClauseItem key={index}>{issue}</ClauseItem>
                      ))}
                    </ClauseList>
                  ) : (
                    'No compliance issues identified'
                  )}
                </AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Recommendations</AnalysisLabel>
                <AnalysisValue>
                  {analysisResult.recommendations && analysisResult.recommendations.length > 0 ? (
                    <RecommendationList>
                      {analysisResult.recommendations.map((recommendation, index) => (
                        <RecommendationItem key={index}>{recommendation}</RecommendationItem>
                      ))}
                    </RecommendationList>
                  ) : (
                    'No recommendations available'
                  )}
                </AnalysisValue>
              </AnalysisItem>
            </AnalysisSection>
          )}

          {predictionResult && (
            <AnalysisSection>
              <AnalysisTitle>
                <FaGavel /> Judgment Prediction: {predictionResult.case_id}
              </AnalysisTitle>
              <AnalysisItem>
                <AnalysisLabel>Predicted Verdict</AnalysisLabel>
                <AnalysisValue>
                  <StatusBadge status={predictionResult.predicted_verdict === 'Favorable' ? 'completed' : 'error'}>
                    {predictionResult.predicted_verdict}
                  </StatusBadge>
                </AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Confidence Score</AnalysisLabel>
                <AnalysisValue>
                  {(predictionResult.confidence_score * 100).toFixed(1)}%
                </AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Key Factors</AnalysisLabel>
                <AnalysisValue>
                  {predictionResult.key_factors && predictionResult.key_factors.length > 0 ? (
                    <ClauseList>
                      {predictionResult.key_factors.map((factor, index) => (
                        <ClauseItem key={index}>{factor}</ClauseItem>
                      ))}
                    </ClauseList>
                  ) : (
                    'No key factors identified'
                  )}
                </AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Legal Reasoning</AnalysisLabel>
                <AnalysisValue>{predictionResult.legal_reasoning}</AnalysisValue>
              </AnalysisItem>
              <AnalysisItem>
                <AnalysisLabel>Recommended Actions</AnalysisLabel>
                <AnalysisValue>
                  {predictionResult.recommended_actions && predictionResult.recommended_actions.length > 0 ? (
                    <RecommendationList>
                      {predictionResult.recommended_actions.map((action, index) => (
                        <RecommendationItem key={index}>{action}</RecommendationItem>
                      ))}
                    </RecommendationList>
                  ) : (
                    'No recommended actions'
                  )}
                </AnalysisValue>
              </AnalysisItem>
            </AnalysisSection>
          )}
        </>
      ) : (
        <FeaturesGrid>
          <FeatureCard>
            <FeatureIcon>
              <FaFileContract />
            </FeatureIcon>
            <FeatureTitle>Document Analysis</FeatureTitle>
            <FeatureDescription>
              Upload contracts and legal documents for detailed analysis, clause identification, and risk assessment.
            </FeatureDescription>
          </FeatureCard>

          <FeatureCard>
            <FeatureIcon>
              <FaSearch />
            </FeatureIcon>
            <FeatureTitle>Legal Insights</FeatureTitle>
            <FeatureDescription>
              Extract key legal information, identify compliance issues, and get actionable recommendations.
            </FeatureDescription>
          </FeatureCard>

          <FeatureCard>
            <FeatureIcon>
              <FaGavel />
            </FeatureIcon>
            <FeatureTitle>Judgment Prediction</FeatureTitle>
            <FeatureDescription>
              Analyze case documents to predict legal outcomes and assess success probabilities.
            </FeatureDescription>
          </FeatureCard>

          <FeatureCard>
            <FeatureIcon>
              <FaChartBar />
            </FeatureIcon>
            <FeatureTitle>Risk Assessment</FeatureTitle>
            <FeatureDescription>
              Identify potential legal risks and get mitigation strategies for your business documents.
            </FeatureDescription>
          </FeatureCard>
        </FeaturesGrid>
      )}
    </PageContainer>
  );
}

export default DocumentPage;
